INSERT INTO `page_translations` (`id`, `page_id`, `locale`, `name`, `description`) VALUES
(5, 3, 'en', 'about-us', 'Al Sahaba Printing Press is one of the leading printing presses in the United Arab Emirates. It was established in 1997 in the Emirate of Abu Dhabi - Al Ain\r\n\r\nThe printing press specializes in printing commercial publications on modern printing machines by skilled technicians in addition to cutting, merging & packaging services according to the best quality standards.\r\n\r\nAll this in order to meet the needs of the customers.'),
(6, 3, 'ar', 'من نحن', 'مطبعة الصحابة هي واحدة من المطابع الرائدة في دولة الإمارات العربية المتحدة. تأسست عام 1997 في إمارة أبوظبي - العين\r\n\r\nالمطبعة متخصصة في طباعة المطبوعات التجارية على ماكينات الطباعة الحديثة بأيدي فنيين مهرة بالإضافة إلى خدمات التقطيع والدمج والتعبئة وفق أفضل معايير الجودة.\r\n\r\nكل هذا من أجل تلبية احتياجات العملاء.'),
(7, 3, 'tr', 'hakkında', 'Al Sahaba Matbaası, Birleşik Arap Emirlikleri\'nin önde gelen matbaalarından biridir. 1997 yılında Abu Dabi Emirliği - Al Ain\'de kuruldu.\r\n\r\nMatbaa, en iyi kalite standartlarına göre kesme, birleştirme ve paketleme hizmetlerine ek olarak, modern baskı makinelerinde vasıflı teknisyenler tarafından ticari yayınların basılması konusunda uzmanlaşmıştır.\r\n\r\nBütün bunlar müşterilerin ihtiyaçlarını karşılamak için.'),
(8, 4, 'en', 'TERMS & CONDITIONS', NULL),
(9, 4, 'ar', 'الاحكام و الشروط', NULL),
(10, 4, 'tr', 'ŞARTLAR VE KOŞULLAR', NULL);
